function WC_Dashboard_Sales(e){this.options={series:{lines:{show:!0,fill:!0},points:{show:!0}},grid:{show:!0,aboveData:!1,color:"#ccc",backgroundColor:"#fff",borderWidth:2,borderColor:"#ccc",clickable:!1,hoverable:!0,markings:this.weekendAreas},xaxis:{mode:"time",timeformat:"%d %b",monthNames:params.month_names,tickLength:1,minTickSize:[1,"day"]},yaxes:[{min:0,tickSize:10,tickDecimals:0},{position:"right",min:0,tickDecimals:2}],colors:["#8a4b75","#47a03e"],legend:{show:!0,position:"nw"}}}WC_Dashboard_Sales.prototype.weekendAreas=function(e){var t=[],n=new Date(e.xaxis.min);n.setUTCDate(n.getUTCDate()-(n.getUTCDay()+1)%7);n.setUTCSeconds(0);n.setUTCMinutes(0);n.setUTCHours(0);var r=n.getTime();do{t.push({xaxis:{from:r,to:r+1728e5}});r+=6048e5}while(r<e.xaxis.max);return t};WC_Dashboard_Sales.prototype.plot=function(){order_data=jQuery.parseJSON(params.order_data.replace(/&quot;/g,'"'));var e=order_data.order_counts,t=order_data.order_amounts;for(var n=0;n<e.length;++n)e[n][0]+=36e5;for(var n=0;n<t.length;++n)t[n][0]+=36e5;var r=jQuery("#placeholder"),i=jQuery.plot(r,[{label:params.number_of_sales,data:e},{label:params.sales_amount,data:t,yaxis:2}],this.options);r.resize();var s=this,o=null;jQuery("#placeholder").bind("plothover",function(e,t,n){if(n){if(o!=n.dataIndex){o=n.dataIndex;jQuery("#tooltip").remove();if(n.series.label==params.number_of_sales){var r=n.datapoint[1];s.showTooltip(n.pageX,n.pageY,params.sold+": "+r)}else{var r=n.datapoint[1].toFixed(2);s.showTooltip(n.pageX,n.pageY,params.earned+": "+params.currency_symbol+r)}}}else{jQuery("#tooltip").remove();o=null}})};WC_Dashboard_Sales.prototype.showTooltip=function(e,t,n){jQuery('<div id="tooltip">'+n+"</div>").css({position:"absolute",display:"none",top:t+5,left:e+5,padding:"5px 10px",border:"3px solid #3da5d5",background:"#288ab7"}).appendTo("body").fadeIn(200)};var wc_dashboard_sales=new WC_Dashboard_Sales;wc_dashboard_sales.plot();